ACTION.CODE:
sel = textArea.selectedText;
if(sel == null)
	view.toolkit.beep();
else
	factorEval(view,sel);

if(textArea.selectionCount == 0)
	textArea.selectWord();
factorEval(view,
	"\""
	+ factor.FactorReader.charsToEscapes(
	textArea.selectedText)
	+ "\" apropos.");

if(textArea.selectedText == null)
	textArea.toolkit.beep();
else
{
	factorEval(view,
		"[ " + textArea.selectedText + " ] balance .");
}
