ACTION.CODE:
sel = textArea.selectedText;
if(sel == null)
	view.toolkit.beep();
else
	FactorPlugin.eval(view,sel);

word = FactorPlugin.getWordAtCaret(textArea);
if(word == null)
	view.toolkit.beep();
else
{
	FactorPlugin.eval(view,
		"\""
		+ FactorReader.charsToEscapes(word)
		+ "\" apropos.");
}

word = FactorPlugin.getWordAtCaret(textArea);
if(word == null)
	view.toolkit.beep();
else
	FactorPlugin.insertUseDialog(view,word);
